import { Form,  message } from "antd";
import './viewInvoice.css'
//import './Sign.css'
import React from "react";
import { Row, Col } from "antd";
import Defaultlayout from "../defaultLayout/defaultLayout";
import logo from '../../image/logo.jpg'

// import Headers from '../dashboard/header/header';
// import Additem from '../Additem/Additem';
// import "./Invoice.css";
import ReactToPrint from "react-to-print";
import PropTypes from "prop-types";

const FormItem = Form.Item;

class Invoice extends React.Component {
  state = {
    localData: ""
  };

  //  handleButtonClick(e) {
  //     message.info('Click on left button.');
  //     console.log('click left button', e);
  //   }

  handleMenuClick(e) {
    message.info("Click on menu item.");
  }

  //   componentDidMount(){
  //       let getLocalData = localStorage.getItem("Data");
  //       let parseData = JSON.parse(getLocalData)
  //       this.setState({ localData: parseData});

  //     }

  render() {
    return (
      <Defaultlayout>
      <h3><strong>View Invoice</strong></h3>
        <Form className="invoiceData">
          <div className="invoice-box">
          <h3><strong>INVOICE</strong></h3>
            <table cellpadding="0" cellspacing="0">

              {/* <h2><strong>INVOICE</strong></h2> */}
              <tr className="top">
                <td colspan="2">
                  <table>
                    <tr>
                    <td>
                        Invoice #: 123 <br />Created: January 1<br /> 2018 Due: February 1,
                        2018
                      </td>
                      <td className="title">
                        <img className='image' src={require('../../image/logo.jpg')} />
                      </td>

                    </tr>
                  </table>
                </td>
              </tr>

              <tr className="information">
                <td colspan="2">
                  <table style={{textAlign:"left"}}>
                    <tr >
                      <td >
                        <strong>From:</strong>
                       <td>example@<br />infiniti7@gmail.com<br />#42, 6th main<br /> jp nagar 8th phase<br/>bangalore</td>
                        </td>


                      <td className='viewTo'>
                        <strong>For:</strong>
                        <br />
                        <td>example@<br />infiniti7@gmail.com<br />#42, 6th main<br /> jp nagar 8th phase<br/>bangalore</td>
                      </td>

                      <tr>

                      <td>

                        <br />
                      </td>
                      </tr>
                      <tr>
                      <td >
                        <strong>From:</strong>
                       <td>example@<br />infiniti7@gmail.com<br />#42, 6th main<br /> jp nagar 8th phase<br/>bangalore</td>
                        </td>
                      </tr>
                    </tr>
                  </table>
                </td>
              </tr>

              <tr className="heading">
                <td>Payment Method</td>

                <td>Check #</td>
              </tr>

              <tr className="details">
                <td>Check</td>

                <td />
              </tr>

              <tr className="heading">
                <td>Item</td>

                <td>Price</td>
              </tr>

              <tr className="item">
                <td>Website design</td>

                <td>$</td>
              </tr>

              <tr className="item">
                <td>Hosting (3 months)</td>

                <td>$</td>
              </tr>

              <tr className="item last">
                <td>Domain name (1 year)</td>

                <td>$</td>
              </tr>

              <tr className="total">
                <td />

                <td>Total: $</td>
              </tr>
            </table>
          </div>
          <div />
        </Form>
      </Defaultlayout>
    );
  }
}

