import React from 'react';
import { Modal, Button, Form, Icon, Input } from 'antd';
const FormItem = Form.Item;
class Email extends React.Component {
  state = {
    ModalText: 'Content of the modal',
    visible: false,
    confirmLoading: false,
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  }

  handleOk = () => {
    this.setState({
      ModalText: 'The modal will be closed after two seconds',
      confirmLoading: true,
    });
    setTimeout(() => {
      this.setState({
        visible: false,
        confirmLoading: false,
      });
    }, 2000);
  }

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  }

  render() {
    const { visible, confirmLoading, ModalText } = this.state;
    return (
      <div>
        <Button type="primary" onClick={this.showModal}>
            Email       
     </Button>
        <Modal title="Email"
          visible={visible}
          onOk={this.handleOk}
          confirmLoading={confirmLoading}
          onCancel={this.handleCancel}
        >
          {/* <p>{ModalText}</p> */}
          <Form>
          <FormItem label='To'>

            <Input  style={{ color: 'rgba(0,0,0,.25)' }} placeholder="eaxmple@gmail.com" />

        </FormItem>
        <FormItem label='Subject'>
               <Input  style={{ color: 'rgba(0,0,0,.25)' }} placeholder="subject" />
        </FormItem>
        <textarea   style={{width:"100%"}}>
        </textarea>
          </Form>
          <div class="ant-modal-footer"><div><button type="button" class="ant-btn"><span>Cancel</span></button><button type="button" class="ant-btn ant-btn-primary"><span>OK</span></button></div></div>
        </Modal>
      </div>
    );
  }
}

export default Email;
